SELECT '25c971a3-f668-40b5-a6bd-c5331e836ed3', 'Punida Pelo Seu Amor', 'https://bushotizgnzejxaqovjy.supabase.co/storage/v1/object/public/images/dramas/covers/757101c2-35f1-42de-8da1-bf53714854c5_pt.webp', 'Sinopse:
